window.addEventListener("DOMContentLoaded", () => {
  let e = document.querySelector('[data-popup="popup__presentation"]'),
    t = document.querySelector(".popup__presentation");
  if (e && t) {
    e.addEventListener("click", function (e) {
      e.preventDefault(),
        t.classList.add("open"),
        t.closest(".popup-custom").classList.add("open");
    });
    let p = t.querySelector('[data-popup="popup__presentation__close"]');
    p &&
      p.addEventListener("click", function () {
        t.classList.remove("open"),
          t.closest(".popup-custom").classList.remove("open");
      });
  }
});
